import React, { useEffect, useState } from "react";

const StarRatings = (props) => {
  const [stars, setStars] = useState("");

  const getStars = (star) => {
    for (let index = 0; index < star; index++) {
        setStars(stars + "⭐")
    }
  }

  useEffect(() => {
    getStars(props.star);
  }, []);

  return <>{stars}</>;
};

export default StarRatings;
