import React, { useEffect, useState } from "react";

const StarRatings = (props) => {
  const [stars, setStars] = useState("");

  const getStars = (star) => {
    switch (star) {
        case 1:
            setStars("⭐")
            break;
        case 2:
            setStars("⭐⭐")
            break;
        case 3:
            setStars("⭐⭐⭐")
            break;
        case 4:
            setStars("⭐⭐⭐⭐")
            break;
        case 5:
            setStars("⭐⭐⭐⭐⭐")
            break;
        default:
            setStars("")
            break;
    }
  }

  useEffect(() => {
    getStars(props.star);
  }, []);

  return <>{getStars(props.star)}
  {stars}</>;
};

export default StarRatings;
